class Random {
  
  seed = 1;
  multiplier = 1664525;
  increment = 1013904223;
  modulus = Math.pow(2, 31);

  constructor() {
    this.seed = this.next();
  }

  next() {
    this.seed = (this.multiplier * this.seed + this.increment) % this.modulus;
    return this.seed;
  }

  nextFloat() {
    return this.next() / this.modulus;
  }

  range(min, max, precision = 4) {
    let n = this.nextFloat() * (max - min) + min;
    return parseFloat(n.toFixed(precision));
  }

  rangeInt(min, max) {
    return this.range(min, max, 0);
  }
}

export const random = new Random();